  background-color: #008CCF;
  color: #fff;
}
.courses .course:nth-child(3) .course__term, .courses .course:nth-child(4) .course__term, .courses .course:nth-child(5) .course__term {
  color: #98F3FF;
}
.courses .course:nth-child(5)::before {
  content: "";
  width: 36px;
  height: 36px;
  background: url("/421325f4000113da69102987f499310c.svg");
  position: absolute;
  left: 5px;
  top: 50px;
}
.courses .course:nth-child(2) .course__title {
  margin-top: 0;
  margin-bottom: 15px;
}
.courses .course__title {
  font-size: 20px;
  line-height: 110%;
  height: 44px;
  margin-top: 15px;
}
.courses .course__image {
  object-fit: contain;
  object-position: top;
  height: 110px;
  width: 80px;
}
.courses .course__term {
  font-size: 36px;
  line-height: 130%;
}
.courses .course__target-title {
  font-size: 24px;
  line-height: 120%;
}
.courses .course__target-value {
  font-size: 48px;
  line-height: 110%;
}
.courses .course__button {
  position: absolute;
  left: 50%;
  transform: translate(-50%);
  bottom: 35px;
  width: 170px;
}
.courses .course__button button {
  padding: 10px;
}
.reviews {
  background: url("/4cbf49fd839b5119b1c3d945cceda510.png") no-repeat center/cover;
  padding-bottom: 75px;
  padding-top: 100px;
}
.reviews__title {
  font-size: 48px;
  text-align: center;
  text-transform: uppercase;
  margin-bottom: 25px;
}
.reviews .nav-container {
  top: -450px;
  z-index: 10;
}
@media screen and (max-width: 480px) {
  .reviews .nav-container {
    top: -550px;
  }
}
.reviews .swiper-container .swiper-pagination-bullets {
  align-items: center;
  justify-content: center;
  display: flex;
}
.reviews .swiper-container .swiper-pagination-bullets .swiper-pagination-bullet {
  opacity: 1;
  width: 30px;
  height: 30px;
  background-color: #BEBEBE;
  -webkit-tap-highlight-color: rgba(255, 255, 255, 0) !important;
  -webkit-focus-ring-color: rgba(255, 255, 255, 0) !important;
  outline: none !important;
}
.reviews .swiper-container .swiper-pagination-bullets .swiper-pagination-bullet:not(:last-child) {
  margin-right: 38px;
}
.reviews .swiper-container .swiper-pagination-bullets .swiper-pagination-bullet.swiper-pagination-bullet-active {
  width: 60px;
  height: 60px;
  background: linear-gradient(259.35deg, #6CB71D 34.08%, #208318 88.43%);
}
@media screen and (max-width: 768px) {
  .reviews .swiper-container .swiper-pagination-bullets .swiper-pagination-bullet.swiper-pagination-bullet-active {
    width: 45px;
    height: 45px;
  }
}
.reviews .swiper-container .swiper-button-next,
.reviews .swiper-container .swiper-button-prev {
  width: 65px;
  height: 65px;
  background: url("/5d51f5965bb7afc91e6daf6325ec00ca.svg");
  background-position: center;
  background-repeat: no-repeat;
  background-size: contain;
  transform: translateX(-25px);
}
@media screen and (max-width: 480px) {
  .reviews .swiper-container .swiper-button-next,
.reviews .swiper-container .swiper-button-prev {
    width: 45px;
    height: 45px;
  }
}
.reviews .swiper-container .swiper-button-next::after,
.reviews .swiper-container .swiper-button-prev::after {
  content: none;
}
.reviews .swiper-container .swiper-button-next.swiper-button-disabled,
.reviews .swiper-container .swiper-button-prev.swiper-button-disabled {
  opacity: 1;
  background-image: url("/b39773efdc50fb41dfc1f7e541ad382c.svg");
}
.reviews .swiper-container .swiper-button-next {
  transform: rotate(180deg) translateX(-25px);
}
.reviews .container:not(.nav-container) {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding-bottom: 100px;
}
.reviews .review-inner {
  margin-right: -70px;
  display: flex;
  padding-top: 40px;
  padding-bottom: 40px;
}
@media screen and (max-width: 768px) {
  .reviews .review-inner {
    margin-right: 0;
    padding-top: 40px;
  }
}
.reviews .review__image {
  width: 250px;
  height: 250px;
  object-fit: cover;
  object-position: center;
  border: 15px solid #fff;
  box-shadow: 1px 1px 25px -10px #717171;
  position: relative;
}
@media screen and (max-width: 768px) {
  .reviews .review__image {
    width: 200px;
    height: 200px;
  }
}
@media screen and (max-width: 568px) {
  .reviews .review__image {
    width: 180px;
    height: 180px;
  }
}
@media screen and (max-width: 480px) {
  .reviews .review__image {
    width: 150px;
    height: 150px;
    border-width: 5px;
  }
}
.reviews .review__image:nth-child(1) {
  transform: rotate(-8deg);
}
.reviews .review__image:nth-child(2) {
  transform: rotate(15deg);
}
@media screen and (max-width: 1200px) {
  .reviews .review .container {
    flex-direction: column;
  }
}
.reviews .description {
  z-index: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 512px;
  height: 512px;
  background: url("/0dcbb5263c7070cb350c18c0ed093b95.svg") no-repeat center/contain;
}
@media screen and (max-width: 768px) {
  .reviews .description {
    width: 440px;
    height: 440px;
  }
}
.reviews .description__title {
  margin-top: 80px;
  width: 350px;
  margin-left: 110px;
  margin-bottom: 15px;
  font-family: "Oswald";
  font-weight: 600;
  font-size: 24px;
  line-height: 136%;
}
@media screen and (max-width: 768px) {
  .reviews .description__title {
    margin-left: 150px;
    margin-bottom: 5px;
  }
}
.reviews .description__body {
  font-size: 16px;
  width: 350px;
  margin-left: 10px;
  font-family: "Roboto";
  font-style: normal;
  font-weight: normal;
  font-size: 16px;
  line-height: 136%;
  overflow: auto;
  height: 290px;
}
@media screen and (max-width: 768px) {
  .reviews .description__body {
    font-size: 15px;
    margin-left: 25px;
    height: 200px;
  }
}
@media screen and (max-width: 480px) {
  .reviews .description__body {
    max-width: 280px;
  }
}

.weight {
  overflow: hidden;
  margin-top: 130px;
  padding-bottom: 150px;
}
.weight__img {
  object-fit: contain;
  object-position: center;
  width: 780px;
  margin-right: 30px;
  margin-left: -250px;
}
@media screen and (max-width: 1200px) {
  .weight__img {
    position: absolute;
    top: 170px;
  }
}
@media screen and (max-width: 768px) {
  .weight__img {
    width: 690px;
    top: 130px;
  }
}
@media screen and (max-width: 480px) {
  .weight__img {
    top: 250px;
  }
}
.weight .container {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
}
.weight .description {
  position: relative;
}
.weight .description__title {
  font-size: 64px;
  text-transform: uppercase;
  line-height: 105%;
}
.weight .description__image {
  position: absolute;
  top: 100px;
}
@media screen and (max-width: 1200px) {
  .weight .description__image {
    top: 600px;
  }
}
@media screen and (max-width: 768px) {
  .weight .description__image {
    top: 530px;
  }
}
@media screen and (max-width: 480px) {
  .weight .description__image {
    top: 750px;
  }
}
.weight .description__subtitle {
  font-size: 36px;
  text-transform: lowercase;
  line-height: 105%;
  margin-bottom: 70px;
}
@media screen and (max-width: 1200px) {
  .weight .description__subtitle {
    margin-bottom: 700px;
  }
}
@media screen and (max-width: 768px) {
  .weight .description__subtitle {
    margin-bottom: 550px;
  }
}
.weight .description__body {
  font-family: Roboto;
  font-style: normal;
  font-weight: normal;
  font-size: 18px;
  line-height: 136%;
}

