sources.styles\\main.scss:
@import 'normalize.css';
@import 'fonts.scss';
@import 'varriables.scss';
*{
    padding: 0;
    margin: 0;
}
.accent{
    color: $blue;
    &.red{
        color: $red;
    }
    &.green{
        color: $green;
    }
}
body{
    font-style: normal;
    font-family: 'Roboto';
    font-weight: normal;
    font-style: normal;
    line-height: 136%;
    font-size: 16px;
    color: $main-color;
    @media screen and (max-width: 570px){
        font-size: 14px;
        line-height: 130%;
    }
}
ul, li{
    list-style: none;
}
h1, h2, h3, h4{
    font-family: 'Oswald';
    font-weight: 700;
}
h1{
    font-size: 72px;
    @media screen and (max-width: 765px){
        font-size: 60px;
    }
    @media screen and (max-width: 570px){
        font-size: 55px;
    }
}
h2{
    font-size: 68px;
    @media screen and (max-width: 765px){
        font-size: 55px;
    }
    @media screen and (max-width: 570px){
        font-size: 45px;
    }
}
h3{
    font-size: 24px;
}
h4{
    font-size: 18px;
}
.container {
    //border: 1px dotted #ff000070;
    position: relative;
    width: 1140px;
    left: 50%;
    transform: translate(-50%);
    @media screen and (max-width: 1200px){
        width: 720px;
    }
    @media screen and (max-width: 765px){
        width: 530px;
    }
    @media screen and (max-width: 570px){
        width: 460px;
    }
    @media screen and (max-width: 480px){
        width: 280px;
    }
}

.header {
    position: relative;
    background: $first-screen-bg;
    background: linear-gradient(180deg, $first-screen-bg 86%, rgba(255,255,255,0) 100%);
    padding-top: 22px;

    overflow-x: hidden;
    &__decor-1,
    &__decor-2,
    &__decor-3{
        display: flex;
        align-items: flex-end;
        position: absolute;
        object-position: left;
        object-fit: contain;
        left: -330px;
        top: -22px;
        width: 1150px;
        height: 810px;
        z-index: -1;
        @media screen and (max-width: 1200px){
            left: -270px;
        }
    }
    &__decor-2{
        left: auto;
        top: auto;
        right: -480px;
        width: 1050px;
        bottom: 20px;
        @media screen and (max-width: 1280px){
            right: -400px;
        }
        @media screen and (max-width: 1200px){
            right: -360px;
            width: 860px;
            height: 1300px;
        }
        @media screen and (max-width: 765px){
            right: -320px;
        }
        @media screen and (max-width: 570px){
            right: -380px;
        }
        @media screen and (max-width: 480px){
            right: -460px;
        }
    }
    &__decor-3{
        height: 150px;
        background: rgb(255,255,255);
        background: linear-gradient(0deg, rgba(255,255,255,1) 0%, rgba(255,255,255,0) 100%);
        left: 0;
        top: auto;
        position: absolute;
        width: 100%;
        z-index: 1;
        bottom: 0;
        img{
            object-position: bottom;
            object-fit: cover;
            width: 100%;
        }
    }
    &__offer{
        margin-top: 15px;
        display: flex;
    }
    .container{
        min-height: 100vh;
    }
}
.head-wrapper{
    display: grid;
    grid-template-areas: 't i' 's i';
    max-width: 642px;
    @media screen and (max-width: 1200px){
        max-width: unset;
        margin-top: 10px;
    }
    @media screen and (max-width: 480px){
        grid-template-areas: 't' 's' 'i';
    }
    @media screen and (max-width: 480px){
        width: 280px;
        display: flex;
        flex-direction: column;
        justify-content: center;
    }
    .offer__title{
        grid-area: t;
    }
    .offer__sub-title{
        grid-area: s;
        white-space: nowrap;
        @media screen and (max-width: 570px){
            white-space: normal;
        }
    }
    .offer__logo{
        grid-area: i;
        margin-top: -10px;
        width: 250px;
        height: 180px;
        object-fit: contain;
        object-position: center;
        @media screen and (max-width: 1200px){
            justify-self: flex-end;
            height: 185px;
        }
        @media screen and (max-width: 765px){
            margin-right: -70px;
        }
        @media screen and (max-width: 570px){
            width: 180px;
            height: 140px;
            margin: 0;
        }
        @media screen and (max-width: 480px){
            width: 250px;
            height: 180px;
            margin: 0;
            align-self: center;
        }
    }
}
.offer {
    display: grid;
    &__sub-title{
        line-height: 100%;
        font-weight: 300;
        margin-top: -25px;
        margin-bottom: 15px;
        @media screen and (max-width: 480px){
            margin-top: -15px;
            margin-bottom: 20px;
        }
    }
    &__description{
        grid-area: d;
        max-width: 540px;
        @media screen and (max-width: 1200px){
            min-width: 540px;
            max-width: unset;
        }
        @media screen and (max-width: 570px){
            min-width: 460px;
            max-width: unset;
        }
        @media screen and (max-width: 480px){
            min-width: 280px;
            width: 280px;
            margin-top: 25px;
        }
        .theses {
            &__item {
                position: relative;
                &:nth-child(1){
                    grid-area: f;
                    margin-right: 66px;
                    
                }
                &:nth-child(2){
                    grid-area: s;
                }
                &:nth-child(3){
                    margin-top: 65px;
                    grid-area: t;
                }
                
                &::before{
                    content: '';
                    margin-top: -25px;
                    transform: translateX(-100%);
                    margin-left: 5px;
                    position: absolute;
                    width: 34px;
                    height: 42px;
                    background: url('../assets/icons/ICON__FLAG.svg') no-repeat center / contain;
                    z-index: -1;
                }
                @media screen and (max-width: 765px){
                    &:nth-child(1){
                        margin: 46px 0 0 0;
                    }
                    &:nth-child(2),
                    &:nth-child(3){
                        margin: 30px 0 0 0;
                    }
                }
            }
        }
        .these {
            max-width: 219px;
            @media screen and (max-width: 765px){
                max-width: unset;
            }
        }
    }
    &__theses {
        margin-top: 33px;
        margin-left: 29px;
        display: grid;
        grid-template-areas: 'f s' 'f t';
        @media screen and (max-width: 1200px){
            margin-top: 70px;
        }
        @media screen and (max-width: 765px){
            display: flex;
            flex-direction: column;
            margin-top: 0;
        }
    }
    &__text{
        font-size: 18px;
    }
    grid-template-areas: 'l a' 't form' 'd form' 'f form';
    @media screen and (max-width: 1200px){
        grid-template-areas: 'l a' 't t' 'd d' 'f form';
    }
    @media screen and (max-width: 765px){
        grid-template-areas: 'l a a' 't t t' 'd d d' 'f f f' 'form form form';
    }
    @media screen and (max-width: 570px){
        width: 460px;
    }
    @media screen and (max-width: 480px){
        grid-template-areas: 'a' 'l' 't' 'f' 'd' 'form';
    }
    &__logo-head{
        width: 194px;
        height: 85px;
        grid-area: l;
        @media screen and (max-width: 570px){
            width: 150px;
            height: 75px;
        }
        @media screen and (max-width: 480px){
            justify-self: left;
        }
    }
    &__awards{
        grid-area: a;
        align-items: flex-start;
        justify-self: end;
        @media screen and (max-width: 480px){
            justify-self: left;
        }
    }
    .head-wrapper{
        grid-area: t;
    }
    &__features{
        grid-area: f;
    }
    &__form{
        grid-area: form;
        @media screen and (max-width: 1200px){
           margin-left: -30px;
        }
    }
}
span.bold{
    font-weight: bold;
    font-size: 16px;
    line-height: 19px;
    color: #000;
    text-transform: uppercase;
}
.button{
    font-family: 'Oswald';
    color: #fff;
    text-transform: uppercase;
    outline: none;
    padding: 17px 20px;
    border-radius: 30px;
    font-weight: bold;
    font-size: 18px;
    border: none;
    background: $main-gradient;
    font-family: 'Open Sans';
    position: relative;
    z-index: 1;
    transition: all .3s ease-in-out;
    cursor: pointer;
    cursor: pointer;
    -webkit-tap-highlight-color: rgba(255, 255, 255, 0) !important; 
    -webkit-focus-ring-color: rgba(255, 255, 255, 0) !important; 
