.features {
    margin-top: 30px;
    max-width: 560px;
    display: grid;
    grid-template-areas: 'i f .' 'i . t' 'i s .';
    @media screen and (max-width: 1200px){
        grid-template-areas: 'i i' 'f .' '. t' 's .';
    }
    @media screen and (max-width: 765px){
        grid-template-areas: 'i f .' 'i . t' 'i s .';
    }
    @media screen and (max-width: 570px){
        grid-template-areas: 'i f' 'i s' 'i t';
    }
    @media screen and (max-width: 480px){
        grid-template-areas: 'i i i' 'f s t';
    }
    @media screen and (max-width: 480px){
        width: 280px;
    }
    &__item {
        @media screen and (max-width: 1200px){
            margin-top: -100px;
        }
        @media screen and (max-width: 570px){
            margin: 0;
        }
        &:nth-child(1){
            grid-area: i;
            margin-top: -45px;
            margin-right: 35px;
            @media screen and (max-width: 1200px){
                margin-top: 25px;
                justify-self: start;
                margin-right: 10px;
                img{
                    width: 300px;
                    height: 380px;
                    object-fit: contain;
                    object-position: center;
                }
            }
            @media screen and (max-width: 765px){
                margin-top: 10px;
                justify-self: start;
                margin-right: -30px;
                img{
                    width: 270px;
                    height: 360px;
                    object-fit: contain;
                    object-position: center;
                }
            }
            @media screen and (max-width: 570px){
                margin-top: -30px;
                justify-self: start;
                margin-right: -30px;
                img{
                    width: 290px;
                    height: 420px;
                    object-fit: contain;
                    object-position: center;
                }
            }
            @media screen and (max-width: 480px){
                img{
                    width: 280px;
                    height: 380px;
                }
            }
        }
        &:nth-child(2){
            grid-area: f;
            margin-bottom: -120px;
            @media screen and (max-width: 1200px){ margin-bottom: -100px; }
            @media screen and (max-width: 765px){ margin: 80px 0 -90px 0; }
            @media screen and (max-width: 570px){ margin: 0; }
            @media screen and (max-width: 480px){ margin: -50px 0 0 0; }
        }
        &:nth-child(3){
            grid-area: t;
            margin-left: 20px;
            @media screen and (max-width: 1200px){ margin-left: -50px; }
            @media screen and (max-width: 765px){ margin: 10px 0 0 -15px; }
            @media screen and (max-width: 570px){ margin: 0; }
        }
        &:nth-child(4){
            margin-top: -120px;
            @media screen and (max-width: 1200px){ margin-top: -130px; }
            @media screen and (max-width: 765px){ margin:  -80px 0 0 0; }
            @media screen and (max-width: 570px){ margin: 0; }
            @media screen and (max-width: 480px){ margin: -20px 0 0 0; }
            grid-area: s;
        }
    }
}
