        color: #FF1400;
        text-transform: uppercase;
        font-size: 18px;
        margin-top: 40px;
        text-align: center;
        z-index: 1;
    }
    position: relative;
    .container {
    }
    .accent {
    }
    .list {
        display: flex;    
        @media screen and (max-width: 1200px){
            flex-wrap: wrap;
        }   
        @media screen and (max-width: 480px){
            justify-content: center;
        }
        &__item {
            &:not(:last-child){
                margin-right: 25px;
            }
            @media screen and (max-width: 1200px){
                margin-top: 60px;
            }
            @media screen and (max-width: 768px){
                margin-top: 40px;
                &:not(:last-child){
                    margin-right: 0;
                }
                &:nth-child(2n + 1){
                    margin-right: 25px;
                }
                margin-left: 25px;
            }
            @media screen and (max-width: 568px){
                margin-left: 0;
            }
            @media screen and (max-width: 480px){
                justify-content: center;
                &:nth-child(2n + 1){
                    margin-right: 0;
                }
            }
        }
    }
    .course {
        font-family: 'Oswald';
        font-weight: bold;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        width: 195px;
        text-align: center;
        position: relative;
        padding: 15px 7px 100px 7px;
        background-color: #DEF1FF;
        border-radius: 5px;
        &:nth-child(3),
        &:nth-child(4),
        &:nth-child(5){
            background-color: #008CCF;
            color: #fff;
            .course__term {
                color: #98F3FF;
            }
        }
        &:nth-child(5){
            &::before{
                content: '';
                width: 36px;
                height: 36px;
                background: url('../assets/icons/ICON__APPLY.svg');
                position: absolute;
                left: 5px;
                top: 50px;
            }
        }
        &:nth-child(2){
            .course__title {
                margin-top: 0;
                margin-bottom: 15px;
            }
        }
        &__title {
            font-size: 20px;
            line-height: 110%;
            height: 44px;
            margin-top: 15px;
        }
        &__image {
            object-fit: contain;
            object-position: top;
            height: 110px;
            width: 80px;
        }
        &__term {
            font-size: 36px;
            line-height: 130%;
        }
        &__target-title {
            font-size: 24px;
            line-height: 120%;
        }
        &__target-value {
            font-size: 48px;
            line-height: 110%;
        }
        &__button {
            position: absolute;
            left: 50%;
            transform: translate(-50%);
            bottom: 35px;
            button{
                padding: 10px;
            }
            width: 170px;
        }
    }
    .button-wrapper {
    }
    .button {
    }
    .button-decor {
    }
}

.reviews {
    background: url('../assets/images/IMG__BG-REVIEW.png') no-repeat center / cover;
    padding-bottom: 75px;
    padding-top: 100px;
    &__title{
        font-size: 48px;
        text-align: center;
        text-transform: uppercase;
        margin-bottom: 25px;
    }
    .nav-container{
        top: -450px;
        z-index: 10;
        @media screen and (max-width: 480px){
            top: -550px;
        }
    }

    .swiper-container {
        .swiper-pagination-bullets {
            align-items: center;
            justify-content: center;
            display: flex;
            .swiper-pagination-bullet {
                opacity: 1;
                width: 30px;
                height: 30px;
                background-color: #BEBEBE;
                -webkit-tap-highlight-color: rgba(255, 255, 255, 0) !important; 
                -webkit-focus-ring-color: rgba(255, 255, 255, 0) !important; 
                outline: none !important;
                &:not(:last-child){
                    margin-right: 38px;
                }
                &.swiper-pagination-bullet-active {
                    width: 60px;
                    height: 60px;
                    background: $main-gradient;
                    @media screen and (max-width: 768px){
                        width: 45px;
                        height: 45px;
                    }
                }
            }            
        }        
        .swiper-button-next,
        .swiper-button-prev {
            width: 65px;
            height: 65px;
            @media screen and (max-width: 480px){
                width: 45px;
                height: 45px;
            }
            background: url('../assets/icons/ICON__BTN-ACTIVE.svg');
            background-position: center;
            background-repeat: no-repeat;
            background-size: contain;
            transform: translateX(-25px);
            &::after{
                content: none;
            }
            &.swiper-button-disabled {
                opacity: 1;
                background-image: url('../assets/icons/ICON__BTN-INACTIVE.svg');
            }
        }
        .swiper-button-next {
            transform: rotate(180deg) translateX(-25px);
        }
    }
    .container:not(.nav-container) {
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;
        padding-bottom: 100px;
    }
    .review-inner {
        margin-right: -70px;
        display: flex;
        padding-top: 40px;
        padding-bottom: 40px;
        @media screen and (max-width: 768px){
            margin-right: 0;
            padding-top: 40px;
        }
    }
    .review {
        &__image {
            width: 250px;
            height: 250px;
            @media screen and (max-width: 768px){
                width: 200px;
                height: 200px;
            }
            @media screen and (max-width: 568px){
                width: 180px;
                height: 180px;
            }
            @media screen and (max-width: 480px){
                width: 150px;
                height: 150px;
                border-width: 5px;
            }
            object-fit: cover;
            object-position: center;
            border: 15px solid #fff;
            box-shadow: 1px 1px 25px -10px #717171;
            position: relative;
            &:nth-child(1){
                transform: rotate(-8deg);
            }
            &:nth-child(2){
                transform: rotate(15deg);
            }
        }
        @media screen and (max-width: 1200px){
            .container{
                flex-direction: column;
            }
        }
    }
    .description {
        z-index: 1;
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 512px;
        height: 512px;
        @media screen and (max-width: 768px){
            width: 440px;
            height: 440px;
        }
        background: url('../assets/images/IMG__COMMENT.svg') no-repeat center / contain;
        &__title {
            margin-top: 80px;
            width: 350px;
            margin-left: 110px;
            margin-bottom: 15px;
            font-family: 'Oswald';
            font-weight: 600;
            font-size: 24px;
            line-height: 136%;
            @media screen and (max-width: 768px){
                margin-left: 150px;
                margin-bottom: 5px;
            }
        }
        &__body {
            font-size: 16px;
            width: 350px;
            margin-left: 10px;
            font-family: 'Roboto';
            font-style: normal;
            font-weight: normal;
            font-size: 16px;
            line-height: 136%;
            overflow: auto;
            height: 290px;
            @media screen and (max-width: 768px){
                font-size: 15px;
                margin-left: 25px;
                height: 200px;
            }
            @media screen and (max-width: 480px){
                max-width: 280px;
            }
        }
    }
}

.weight {
    overflow: hidden;
    margin-top: 130px;
    padding-bottom: 150px;
    &__img {
        object-fit: contain;
        object-position: center;
        width: 780px;
        margin-right: 30px;
        margin-left: -250px;
        @media screen and (max-width: 1200px){
            position: absolute;
            top: 170px;
        }
        @media screen and (max-width: 768px){
            width: 690px;
            top: 130px;
        }
        @media screen and (max-width: 480px){
            top: 250px;
        }
    }
    &__description {
    }
    .container {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: center;
    }
    .description {
        position: relative;
        &__title {
            font-size: 64px;
            text-transform: uppercase;
            line-height: 105%;
        }
        &__image {
            position: absolute;
            top: 100px;
            @media screen and (max-width: 1200px){
                top: 600px;
            }
            @media screen and (max-width: 768px){
                top: 530px;
            }
            @media screen and (max-width: 480px){
                top: 750px;
            }
        }
        &__subtitle {
            font-size: 36px;
            text-transform: lowercase;
            line-height: 105%;
            margin-bottom: 70px;
            @media screen and (max-width: 1200px){
                margin-bottom: 700px;
            }
            @media screen and (max-width: 768px){
                margin-bottom: 550px;
            }
        }
        &__body {
            font-family: Roboto;
            font-style: normal;
            font-weight: normal;
            font-size: 18px;
            line-height: 136%;
        }
    }
    
}
