  width: 100%;
  height: 100%;
  background: #15700D;
  position: absolute;
  left: 0;
  top: auto;
  border-radius: 30px;
  bottom: -3px;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
}
.form__info {
  font-size: 11px;
  line-height: 110%;
  margin-top: 20px;
  width: 216px;
}

.input-wrapper {
  position: relative;
  width: 100%;
}
.input-wrapper label {
  display: inline-block;
  margin-bottom: 9px;
  font-size: 15px;
}
.input-wrapper label::after {
  content: " *";
  color: #FF1000;
}
.input-wrapper:not(:first-child) {
  margin-top: 20px;
}
.input-wrapper .form__input {
  width: 100%;
  background-color: #F0F0F0;
  border-radius: 3px;
  border: none;
  padding: 10px 15px;
  outline: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  text-indent: 1px;
  text-overflow: "";
  border: 1px solid transparent;
  color: #838383;
}
.input-wrapper .form__input:focus {
  background: #fff;
  border-color: #6CB71D;
  color: #1D1D1D;
}
.input-wrapper .form__input.input {
  width: calc(100% - 32px);
}
.input-wrapper .arrow {
  position: absolute;
  bottom: 13px;
  right: 15px;
  width: 10px;
  height: 12px;
  background: url("/75155b130c59adf58a63c2ba8153ecd9.svg") no-repeat center/contain;
}

.timer {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.timer__info {
  font-size: 8px;
}
.timer .timer-wrapper {
  display: flex;
}
.timer .timer-item {
  width: 31px;
  height: 30px;
  border-radius: 5px;
  background: linear-gradient(3.33deg, #FF0000 2.31%, #FF7A00 96.84%);
  color: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
}
.timer .timer-item:not(:last-child) {
  margin-right: 6px;
}
.timer .timer-item::after {
  content: attr(data-title);
  position: absolute;
  bottom: -20px;
  left: 50%;
  transform: translateX(-50%);
