  font-size: 18px;
  line-height: 100%;
  margin-top: 10px;
}

.text-after {
  margin-top: 5px;
}

.text-price {
  font-size: 36px;
  color: #6CB71D;
  margin: 0 10px;
}

.crossed {
  text-decoration: line-through;
}

.features {
  margin-top: 30px;
  max-width: 560px;
  display: grid;
  grid-template-areas: "i f ." "i . t" "i s .";
}
.features__item:nth-child(1) {
  grid-area: i;
  margin-top: -45px;
  margin-right: 35px;
}
.features__item:nth-child(2) {
  grid-area: f;
  margin-bottom: -100px;
}
.features__item:nth-child(3) {
  grid-area: t;
}
.features__item:nth-child(4) {
  margin-top: -100px;
  grid-area: s;
}

.item {
  width: 120px;
  height: 120px;
  border-radius: 50%;
  background: linear-gradient(259.35deg, #6CB71D 34.08%, #208318 88.43%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: "Oswald";
  font-weight: bold;
  font-size: 18px;
  color: #fff;
}

.theses__item {
  position: relative;
}
.theses__item:nth-child(1) {
  grid-area: f;
  margin-right: 66px;
}
.theses__item:nth-child(2) {
  grid-area: s;
}
.theses__item:nth-child(3) {
  margin-top: 65px;
  grid-area: t;
}
.theses__item::before {
  content: "";
  margin-top: -25px;
  transform: translateX(-100%);
  margin-left: 5px;
  position: absolute;
  width: 34px;
  height: 42px;
  background: url("/d047228b9f1dc0dfc9890e9ee6a34684.svg") no-repeat center/contain;
  z-index: -1;
}

.these {
  max-width: 219px;
}

.awards {
  display: flex;
  margin-right: 175px;
}
.awards__item:not(:last-child) {
  margin-right: 14px;
}

.award__img {
  object-fit: contain;
  object-position: center;
}

.answers {
  height: 100vh;
  background: #fff;
}

